export const Home = () => {
  const [products, setProducts] = useState([]);
  const { data } = useLoaderData();
  console.log("data", data);
  useEffect(() => {
    setProducts(data);
  }, [data]);
  console.log("products", products);

  return (
    <div>
      {products.length > 0 ? (
        <div>
          <h1>Products:</h1>
          {products.map((product) => (
            <div key={product.id}>
              <h2>{product.title}</h2>
              <p>{product.description}</p>
            </div>
          ))}
        </div>
      ) : (
        <div>Loading...</div>
      )}
    </div>
  );
};
