import { useEffect, useState } from "react"
// import useInfiniteScroll from '../../hooks/useInfiniteScroll';
import './home.css';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';

export const Home = () => {
  const [products, setProducts] = useState([]);
  // const [resultsCount, setResultsCount] = useState(5);
  // const [isFetching] = useInfiniteScroll(handleFetchMoreItems);

  useEffect(()=>{
    async function fetchData() {
      let url = "https://fakestoreapi.com/products?limit=10";
      const result = await fetch(url);
      const data = await result.json();
      setProducts(data);
    }
    fetchData();
  },[])

  // async function handleFetchMoreItems() {
  //   const result = await fetch(`https://fakestoreapi.com/products?limit=${resultsCount}`);
  //   const data = await result.json();
  //   setProducts([...products, ...data]);
  //   setResultsCount(resultsCount + 5);
  // }

  return (
    <div>
       {products?.map((product, key) => {
        const {title, oldPrice, price, image, category, isNew} =product;
        return (
        <Card sx={{ maxWidth: 445 , maxHeight:600 }}>
          <CardMedia
            component="img"
            alt="green iguana"
            height="140"
            image={image}
          />
          <CardContent>
            <Typography gutterBottom variant="h5" component="div">
              {title}
            </Typography>
            <Typography variant="body2" color="text.secondary">
              Lizards are a widespread group of squamate reptiles, with over 6,000
              species, ranging across all continents except Antarctica
            </Typography>
          </CardContent>
          <CardActions>
            <Button size="small">Share</Button>
            <Button size="small">Learn More</Button>
          </CardActions>
        </Card>
        )
       })}
      {/* {isFetching && <p>Loading...</p>}
       <div id="scrollObserver"></div> */}
    </div>
  )
}
