import { useDispatch, useSelector } from "react-redux";
import CloseIcon from '@mui/icons-material/Close';
import { decrementQantity, deleteFromCart, incrementQuantity, resetCart } from "../redux/threadsSlice";
import './cartitem.css'
import Button from '@mui/material/Button';
import RemoveIcon from '@mui/icons-material/Remove';
import AddIcon from '@mui/icons-material/Add';

export const CartItem = () => {
  const dispatch = useDispatch()

  const productData = useSelector((state)=> state.threads.productData)

  return (
    <div className="cart__item">
      <div className="cart__item__text">
        <h2 className="font-titleFont text-2xl">Shopping cart</h2>
      </div>
      <div>
        {
         productData.map((item, key)=>{
          return <div key={item._id} className="cart__product__parent">
            <div className="cart__product__parent-icon-image">
              <CloseIcon onClick={()=> dispatch(deleteFromCart(item._id))}  className="text-xl text-gray-600 hover:text-red-600 cursor-pointer duration-300"/>
              <img src={item.image} className="cart__product__image" alt="" />
            </div> 
            <h2 className="cart__product__title">{item.title}</h2>
            <div className='cart__product__quantity'>
                  <p>Quantity</p>
                  <div className='cart__product__add-remove'>
                    <RemoveIcon class="cart__product-remove-icon" onClick={()=> dispatch(decrementQantity({
                      _id: item._id,
                      title: item.title,
                      image: item.image,
                      price: item.price,
                      quantity: 1,
                      description: item.description
                    }))}
                     className="">-</RemoveIcon>
                    <span>{item.quantity}</span>
                    <AddIcon onClick={()=> dispatch(incrementQuantity({
                       _id: item._id,
                       title: item.title,
                       image: item.image,
                       price: item.price,
                       quantity: 1,
                       description: item.description

         }))} className="">+</AddIcon>
                  </div>
                </div>
            <p className="w-10">${item.price * item.quantity}</p>
          </div>
         })
        }
        <Button onClick={()=> dispatch(resetCart())} className='cart__item--reset-cart'>Reset Cart</Button>

      </div>
    </div>
  )
}
