import { useEffect, useState } from "react"
import useInfiniteScroll from '../../hooks/useInfiniteScroll';
import './home.css';
import { useLoaderData } from "react-router-dom"
import {Header} from '../../components/Header/Header';
import { ProductDetail } from "../../components/ProductDetail/ProductDetail";

export const Home = () => {
  const [products, setProducts] = useState([]);
  const [isFetching, setIsFetching] = useInfiniteScroll(handleFetchMoreItems);
  const data = useLoaderData();
  useEffect(()=>{
    setProducts(data.products);
  },[data])

  async function handleFetchMoreItems() {
    const nextResultsCount = 8; // Number of items to fetch
    const result = await fetch(`https://dummyjson.com/products?limit=${nextResultsCount}&skip=${products.length}`);
    const data = await result.json();
    setProducts([...products, ...data.products]);
    setIsFetching(false);
  }



  return (
    <div>
    <Header/>
    <div className="home__cart">
       {products && products?.map((product, key) => {
        const {title, price, thumbnail, category} =product;

        return (
          <ProductDetail
          product = {product}
          title={title}
           price ={price}
            thumbnail ={thumbnail}
             category ={category}
          />
        )
       })}
      {isFetching && <p>Loading...</p>}
       <div id="scrollObserver"></div>
    </div>
    </div>
  )
}
