import { useEffect, useState } from "react"
// import useInfiniteScroll from '../../hooks/useInfiniteScroll';
import './home.css';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { useNavigate } from 'react-router-dom';
import { useLoaderData } from "react-router-dom"
import {Header} from '../../components/Header';

export const Home = () => {
  const [products, setProducts] = useState([]);
  // const [resultsCount, setResultsCount] = useState(5);
  // const [isFetching] = useInfiniteScroll(handleFetchMoreItems);
  const data = useLoaderData();
  useEffect(()=>{
    setProducts(data);
  },[data])

  // async function handleFetchMoreItems() {
  //   const result = await fetch(`https://fakestoreapi.com/products?limit=${resultsCount}`);
  //   const data = await result.json();
  //   setProducts([...products, ...data]);
  //   setResultsCount(resultsCount + 5);
  // }

  const navigate = useNavigate()



  return (
    <div>
    <Header/>
    <div className="home__cart">

       {products && products?.map((product, key) => {
        const {title, price, image, category} =product;
        const _id = title;

        const idString = (Title)=>{
          const newIdString = String(Title).toLowerCase().split(" ").join("");
          return newIdString;
        } 
        const rootId = idString(_id)

        const handeImageClick = ()=>{
          navigate(`/product/${rootId}`,{
            state: {
              item: product
            }
          })
        }

        return (
          <div >
          <Card sx={{ maxWidth: 445 }}>
            <CardMedia
              component="img"
              alt="green iguana"
              height="340"
              image={image}
              onClick={handeImageClick}
            />
            <CardContent>
              <Typography gutterBottom variant="h5" component="div">
                {title}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {price}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {category}
              </Typography>
            </CardContent>
            <CardActions>
              <Button size="small">Add to Cart</Button>

            </CardActions>
          </Card>
        </div>
        )
       })}
      {/* {isFetching && <p>Loading...</p>}
       <div id="scrollObserver"></div> */}
    </div>
    </div>
  )
}
