import './App.css';
import { createBrowserRouter, RouterProvider, ScrollRestoration } from 'react-router-dom';

// import { productsData } from './api/api';
import { Home } from './pages/Home/Home';

  const Layout = ()=>{
    return <div>
      <Header/>

    </div>
  }

  const router = createBrowserRouter([
    {
      path:'/',
      element: <Layout/>,
      children:[
        {
          path: '/',
          element: <Home/>,
          loader: productsData
        },
        {
          path:'/product/:id',
          element: <Product/>
        },
        {
          path: '/cart',
          element: <Cart/>
        },
        {
          path: '/login',
          element: <Login/>
        }
      ]
    }
  ]) 


  function App() {
    return (
      <div className="app">
        <RouterProvider router={router}/>
      </div>
    );
  }

export default App;
