import { useEffect, useState } from "react"
import useInfiniteScroll from '../../hooks/useInfiniteScroll'
export const Home = () => {
  const [products, setProducts] = useState([]);
  const [resultsCount, setResultsCount] = useState(5);
  const [isFetching] = useInfiniteScroll(handleFetchMoreItems);

  useEffect(()=>{
    async function fetchData() {
      let url = "https://fakestoreapi.com/products?limit=5";
      const result = await fetch(url);
      const data = await result.json();
      setProducts(data);
    }
    fetchData();
  },[])

  async function handleFetchMoreItems() {
    const result = await fetch(`https://fakestoreapi.com/products?limit=${resultsCount}`);
    const data = await result.json();
    setProducts([...products, ...data]);
    setResultsCount(resultsCount + 5);
  }

  // useEffect(() => {
  //   const observer = new IntersectionObserver((entries) => {
  //     const firstEntry = entries[0];
  //     if (firstEntry.isIntersecting) {
  //       fetchMoreData();
  //     }
  //   }, { threshold: 1 });

  //   observer.observe(document.querySelector('#scrollObserver'));
  //   return () => observer.unobserve(document.querySelector('#scrollObserver'));
  // }, []);


  return (
    <div>
       {products?.map((product, key) => {
        const {title, oldPrice, price, image, category, isNew} =product;
        return (
          <div>
            <div  className="w-full h-96 cursor-pointer overflow-hidden">
        <img  className=' h-full w-full object-cover group-hover:scale-110 duration-500'
         src={image} alt="" />
      </div>
      <div className="w-full border-[1px] px-2 py-4">
          <div>
            <h2 className='font-arial text-xl font-bold '>{title}</h2>
          </div>
          <div className='flex justify-between items-center'>
          <p>{category}</p>
          <div className='flex gap-2 justify-end'>
            <p className='line-through text-gray-500'>${price}</p>
            <p className='font-semibold'>${oldPrice}</p>
          </div>

        </div> 
        <div className='flex justify-between mt-2'>
          {/* <button onClick={()=>dispatch(addToCart({
            _id: product._id, 
            title: product.title,
            image: product.image,
            price: product.price,
            quantity: 1,
            description: product.description
          })) && toast.success(`${product.title} is added`)} className='bg-light-gray text-blue-600 hover:text-blue-400'>
            add To Cart
          </button> */}
        </div>
       <div className='top-2 right-2 absolute'>
          {isNew && <p className='bg-black  text-white font-semibold px-6 py-1'>Sale</p>}
        </div>
      </div>
          </div>

        )
       })}
      {isFetching && <p>Loading...</p>}
       <div id="scrollObserver"></div>
    </div>
  )
}
