import { useEffect, useState } from "react"
import useInfiniteScroll from '../../hooks/useInfiniteScroll';
import './home.css';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { useLoaderData } from "react-router-dom"
import {Header} from '../../components/Header';
import { addToCart } from '../../redux/threadsSlice';

export const Home = () => {
  const [products, setProducts] = useState([]);
  const dispatch = useDispatch() ;
  const [resultsCount, setResultsCount] = useState(16);
  const [isFetching, setIsFetching] = useInfiniteScroll(handleFetchMoreItems);
  const data = useLoaderData();
  useEffect(()=>{
    setProducts(data.products);
  },[data])

  async function handleFetchMoreItems() {
    const nextResultsCount = 5; // Number of items to fetch
    const result = await fetch(`https://dummyjson.com/products?limit=${nextResultsCount}&skip=${products.length}`);
    const data = await result.json();
    setProducts([...products, ...data]);
    setResultsCount(resultsCount + 16);
    setIsFetching(false);
  }
  console.log('products',products);
  const navigate = useNavigate()



  return (
    <div>
    <Header/>
    <div className="home__cart">
       {products && products?.map((product, key) => {
        const {title, price, image, category} =product;
        const id = title;

        const idString = (Title)=>{
          const newIdString = String(Title).toLowerCase().split(" ").join("");
          return newIdString;
        } 
        const rootId = idString(id)
        const handeImageClick = ()=>{
          navigate(`/product/${rootId}`,{
            state: {
              item: product
            }
          })
        }

        return (
          <Card sx={{ maxWidth: 445 }} className="card" key={product.id}>
            <CardMedia
              component="img"
              alt="green iguana"
              height="340"
              image={image}
              onClick={handeImageClick}
            />
            <CardContent>
              <Typography gutterBottom variant="h5" component="div">
                {title}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {price}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {category}
              </Typography>
            </CardContent>
            <CardActions>
            <Button onClick={()=>dispatch(addToCart({
                  id: product.id, 
                  title: product.title,
                  image: product.image,
                  price: product.price,
                  quantity: 1,
                  description: product.description
                }))}>Add to Cart</Button>

            </CardActions>
          </Card>
        )
       })}
      {isFetching && <p>Loading...</p>}
       <div id="scrollObserver"></div>
    </div>
    </div>
  )
}
